     1→import { useState } from 'react';
     2→import { exportConfig, serializeBundle, encrypt } from '../lib/config';
     3→import type { ConfigBundle } from '../lib/config/serializer';
     4→import { QRCodeSVG } from 'qrcode.react';
     5→
     6→interface ExportConfigurationProps {
     7→  className?: string;
     8→}
     9→
    10→// QR Code capacity for version 40-L (most common scan target)
    11→const MAX_QR_DATA_SIZE = 2953;
    12→
    13→export default function ExportConfiguration({ className = '' }: ExportConfigurationProps) {
    14→  const [showPassphraseModal, setShowPassphraseModal] = useState(false);
    15→  const [showResultModal, setShowResultModal] = useState(false);
    16→  const [passphrase, setPassphrase] = useState('');
    17→  const [confirmPassphrase, setConfirmPassphrase] = useState('');
    18→  const [error, setError] = useState('');
    19→  const [isExporting, setIsExporting] = useState(false);
    20→  const [exportUrl, setExportUrl] = useState('');
    21→  const [copySuccess, setCopySuccess] = useState(false);
    22→  const [qrCodeTooLarge, setQrCodeTooLarge] = useState(false);
    23→
    24→  const handleStartExport = () => {
    25→    setShowPassphraseModal(true);
    26→    setError('');
    27→    setPassphrase('');
    28→    setConfirmPassphrase('');
    29→  };
    30→
    31→  const handleExport = async () => {
    32→    setError('');
    33→    
    34→    // Validate passphrase
    35→    if (!passphrase) {
    36→      setError('Please enter a passphrase');
    37→      return;
    38→    }
    39→    
    40→    if (passphrase !== confirmPassphrase) {
    41→      setError('Passphrases do not match');
    42→      return;
    43→    }
    44→
    45→    if (passphrase.length < 8) {
    46→      setError('Passphrase must be at least 8 characters long');
    47→      return;
    48→    }
    49→
    50→    setIsExporting(true);
    51→
    52→    try {
    53→      // Export configuration
    54→      const bundle: ConfigBundle = await exportConfig();
    55→
    56→      // Debug: log bundle structure and sizes
    57→      console.log('=== Export Debug ===');
    58→      console.log('Version:', bundle.version);
    59→      console.log('Accounts:', bundle.accounts.length, 'items');
    60→      console.log('Calendars:', bundle.calendars.length, 'items');
    61→      console.log('Account data:', bundle.accounts.map(a => ({
    62→        email: a.email,
    63→        needsReauth: a.needsReauth,
    64→      })));
    65→      console.log('Calendar data:', bundle.calendars.map(c => ({
    66→        id: c.id,
    67→        summary: c.summary,
    68→        summaryLen: c.summary.length,
    69→      })));
    70→
    71→      // Serialize to JSON
    72→      const serialized = serializeBundle(bundle);
    73→
    74→      console.log('Config JSON size:', serialized.length, 'bytes');
    75→
    76→      // Encrypt with passphrase
    77→      const encrypted = await encrypt(serialized, passphrase);
    78→
    79→      console.log('Encrypted size:', encrypted.length, 'bytes');
    80→
    81→      // Generate URL
    82→      const baseUrl = window.location.origin + window.location.pathname;
    83→      const url = `${baseUrl}#config=${encrypted}`;
    84→
    85→      console.log('Final URL size:', url.length, 'bytes');
    86→      console.log('==================');
    87→
    88→      setExportUrl(url);
    89→      setShowPassphraseModal(false);
    90→      setShowResultModal(true);
    91→      setCopySuccess(false);
    92→
    93→      // Check if QR code can handle this data
    94→      setQrCodeTooLarge(url.length > MAX_QR_DATA_SIZE);
    95→    } catch (err) {
    96→      console.error('Export failed:', err);
    97→      setError(err instanceof Error ? err.message : 'Export failed. Please try again.');
    98→    } finally {
    99→      setIsExporting(false);
   100→    }
   101→  };
   102→
   103→  const handleCopy = async () => {
   104→    try {
   105→      await navigator.clipboard.writeText(exportUrl);
   106→      setCopySuccess(true);
   107→      setTimeout(() => setCopySuccess(false), 2000);
   108→    } catch (err) {
   109→      console.error('Failed to copy:', err);
   110→      setError('Failed to copy to clipboard');
   111→    }
   112→  };
   113→
   114→  const handleClosePassphraseModal = () => {
   115→    setShowPassphraseModal(false);
   116→    setPassphrase('');
   117→    setConfirmPassphrase('');
   118→    setError('');
   119→  };
   120→
   121→  const handleCloseResultModal = () => {
   122→    setShowResultModal(false);
   123→    setExportUrl('');
   124→    setCopySuccess(false);
   125→  };
   126→
   127→  return (
   128→    <>
   129→      <button
   130→        className={className}
   131→        onClick={handleStartExport}
   132→      >
   133→        Export Configuration
   134→      </button>
   135→
   136→      {/* Passphrase Input Modal */}
   137→      {showPassphraseModal && (
   138→        <div className="modal-overlay" onClick={handleClosePassphraseModal}>
   139→          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
   140→            <h2>Export Configuration</h2>
   141→            <p className="modal-description">
   142→              Create a secure passphrase to encrypt your configuration. You'll need this passphrase
   143→              to import your configuration on another device.
   144→            </p>
   145→            <div style={{
   146→              background: '#e3f2fd',
   147→              border: '1px solid #2196f3',
   148→              borderRadius: '4px',
   149→              padding: '12px',
   150→              marginTop: '12px',
   151→              marginBottom: '12px'
   152→            }}>
   153→              <p style={{ margin: '0 0 8px 0', color: '#0d47a1', fontWeight: 500 }}>
   154→                What's included:
   155→              </p>
   156→              <ul style={{ margin: 0, paddingLeft: '20px', color: '#0d47a1', fontSize: '14px' }}>
   157→                <li>Account information (email, created date)</li>
   158→                <li>Calendar settings and preferences</li>
   159→                <li>OAuth credentials (client ID, client secret)</li>
   160→                <li>Sync settings and filters</li>
   161→              </ul>
   162→              <p style={{ margin: '8px 0 0 0', color: '#0d47a1', fontWeight: 500, fontSize: '14px' }}>
   163→                ⚠️ Authentication tokens are NOT exported. You'll need to re-authenticate each account on the new device.
   164→              </p>
   165→            </div>
   166→
   167→            {error && (
   168→              <div className="modal-error">
   169→                {error}
   170→              </div>
   171→            )}
   172→
   173→            <div className="form-group">
   174→              <label htmlFor="export-passphrase">Passphrase</label>
   175→              <input
   176→                id="export-passphrase"
   177→                type="password"
   178→                value={passphrase}
   179→                onChange={(e) => setPassphrase(e.target.value)}
   180→                placeholder="Enter a secure passphrase"
   181→                disabled={isExporting}
   182→                autoFocus
   183→              />
   184→            </div>
   185→
   186→            <div className="form-group">
   187→              <label htmlFor="export-confirm-passphrase">Confirm Passphrase</label>
   188→              <input
   189→                id="export-confirm-passphrase"
   190→                type="password"
   191→                value={confirmPassphrase}
   192→                onChange={(e) => setConfirmPassphrase(e.target.value)}
   193→                placeholder="Confirm your passphrase"
   194→                disabled={isExporting}
   195→                onKeyPress={(e) => {
   196→                  if (e.key === 'Enter') {
   197→                    handleExport();
   198→                  }
   199→                }}
   200→              />
   201→            </div>
   202→
   203→            <div className="modal-actions">
   204→              <button
   205→                className="button-secondary"
   206→                onClick={handleClosePassphraseModal}
   207→                disabled={isExporting}
   208→              >
   209→                Cancel
   210→              </button>
   211→              <button
   212→                className="button-primary"
   213→                onClick={handleExport}
   214→                disabled={isExporting}
   215→              >
   216→                {isExporting ? 'Exporting...' : 'Export'}
   217→              </button>
   218→            </div>
   219→          </div>
   220→        </div>
   221→      )}
   222→
   223→      {/* Result Modal */}
   224→      {showResultModal && (
   225→        <div className="modal-overlay" onClick={handleCloseResultModal}>
   226→          <div className="modal-content export-result-modal" onClick={(e) => e.stopPropagation()}>
   227→            <h2>Configuration Exported!</h2>
   228→            <p className="modal-description">
   229→              {!qrCodeTooLarge
   230→                ? 'Scan the QR code or copy the URL below to transfer your configuration to another device.'
   231→                : 'Your configuration is too large for a QR code. Please use the "Copy" button to transfer the URL below.'}
   232→            </p>
   233→
   234→            <div style={{
   235→              background: '#fff3cd',
   236→              border: '1px solid #ffc107',
   237→              borderRadius: '4px',
   238→              padding: '12px',
   239→              marginTop: '12px',
   240→              marginBottom: '12px'
   241→            }}>
   242→              <p style={{ margin: '0 0 8px 0', color: '#856404', fontWeight: 500 }}>
   243→                ⚠️ Important: Re-authentication Required
   244→              </p>
   245→              <p style={{ margin: 0, color: '#856404', fontSize: '14px' }}>
   246→                When you import this configuration on another device, you will need to re-authenticate each account. This is intentional for security reasons - authentication tokens are not transferred.
   247→              </p>
   248→            </div>
   249→
   250→            {error && (
   251→              <div className="modal-error">
   252→                {error}
   253→              </div>
   254→            )}
   255→
   256→            {/* QR Code - only show if data fits */}
   257→            {!qrCodeTooLarge && (
   258→              <div className="qr-code-container">
   259→                <QRCodeSVG
   260→                  value={exportUrl}
   261→                  size={200}
   262→                  level="L"
   263→                  bgColor="#ffffff"
   264→                  fgColor="#000000"
   265→                  includeMargin={true}
   266→                />
   267→              </div>
   268→            )}
   269→
   270→            {qrCodeTooLarge && (
   271→              <div className="qr-code-warning">
   272→                <p>
   273→                  <strong>Configuration too large for QR code</strong>
   274→                </p>
   275→                <p>
   276→                  Your configuration is {exportUrl.length} bytes, but QR codes can only handle 
   277→                  up to {MAX_QR_DATA_SIZE} bytes reliably.
   278→                </p>
   279→                <p>
   280→                  This usually happens when you have many accounts or calendars.
   281→                  Please use the "Copy" button below to transfer your configuration.
   282→                </p>
   283→              </div>
   284→            )}
   285→
   286→            <div className="form-group">
   287→              <label htmlFor="export-url">
   288→                Export URL ({exportUrl.length} bytes)
   289→                {qrCodeTooLarge && ' - Too large for QR code'}
   290→              </label>
   291→              <div className="url-input-group">
   292→                <input
   293→                  id="export-url"
   294→                  type="text"
   295→                  value={exportUrl}
   296→                  readOnly
   297→                  className="url-input"
   298→                  autoFocus
   299→                  onFocus={(e) => e.target.select()}
   300→                />
   301→                <button
   302→                  className="button-copy"
   303→                  onClick={handleCopy}
   304→                  type="button"
   305→                >
   306→                  {copySuccess ? 'Copied!' : 'Copy'}
   307→                </button>
   308→              </div>
   309→              {!qrCodeTooLarge && (
   310→                <p className="form-help">
   311→                  <strong>How to transfer:</strong>
   312→                  <br />
   313→                  • Scan the QR code with your phone's camera
   314→                  <br />
   315→                  • Or copy the URL and send it to yourself
   316→                  <br />
   317→                  • Open the URL on another device and enter your passphrase
   318→                </p>
   319→              )}
   320→              {qrCodeTooLarge && (
   321→                <p className="form-help">
   322→                  <strong>How to transfer:</strong>
   323→                  <br />
   324→                  • Click "Copy" button above
   325→                  <br />
   326→                  • Send the URL to yourself (email, messaging app, etc.)
   327→                  <br />
   328→                  • Open the URL on another device and enter your passphrase
   329→                </p>
   330→              )}
   331→            </div>
   332→
   333→            <div className="modal-actions">
   334→              <button
   335→                className="button-primary"
   336→                onClick={handleCloseResultModal}
   337→              >
   338→                Done
   339→              </button>
   340→            </div>
   341→          </div>
   342→        </div>
   343→      )}
   344→    </>
   345→  );
   346→}
