     1â†’/**
     2â†’ * Configuration transfer encryption using Web Crypto API
     3â†’ *
     4â†’ * Encrypts configuration data with a user-provided passphrase for cross-device transfer.
     5â†’ * Uses PBKDF2 for key derivation and AES-GCM for authenticated encryption.
     6â†’ */
     7â†’
     8â†’// Current format version for binary encoding
     9â†’export const CURRENT_FORMAT_VERSION = 1;
    10â†’
    11â†’// Configuration for PBKDF2 key derivation
    12â†’const PBKDF2_CONFIG = {
    13â†’  algorithm: 'PBKDF2' as const,
    14â†’  hash: 'SHA-256' as const,
    15â†’  iterations: 100000, // OWASP recommended minimum as of 2024
    16â†’  saltLength: 16, // 128 bits
    17â†’};
    18â†’
    19â†’// Configuration for AES-GCM encryption
    20â†’const AES_CONFIG = {
    21â†’  algorithm: 'AES-GCM' as const,
    22â†’  keyLength: 256, // bits
    23â†’  ivLength: 12, // bytes (96 bits, recommended for GCM)
    24â†’};
    25â†’
    26â†’/**
    27â†’ * Result structure for encrypted data
    28â†’ * Contains all components needed for decryption
    29â†’ */
    30â†’interface EncryptedData {
    31â†’  salt: string; // base64-encoded salt
    32â†’  iv: string; // base64-encoded IV
    33â†’  data: string; // base64-encoded ciphertext
    34â†’}
    35â†’
    36â†’/**
    37â†’ * Derives an encryption key from a passphrase using PBKDF2
    38â†’ * @param passphrase - User-provided passphrase
    39â†’ * @param salt - Salt for key derivation (base64-encoded)
    40â†’ * @returns CryptoKey for AES-GCM encryption/decryption
    41â†’ */
    42â†’async function deriveKey(passphrase: string, salt: Uint8Array): Promise<CryptoKey> {
    43â†’  const encoder = new TextEncoder();
    44â†’  const passphraseBytes = encoder.encode(passphrase);
    45â†’
    46â†’  // Import passphrase as a key
    47â†’  const passphraseKey = await crypto.subtle.importKey(
    48â†’    'raw',
    49â†’    passphraseBytes,
    50â†’    'PBKDF2',
    51â†’    false,
    52â†’    ['deriveKey']
    53â†’  );
    54â†’
    55â†’  // Derive the actual encryption key
    56â†’  return crypto.subtle.deriveKey(
    57â†’    {
    58â†’      name: PBKDF2_CONFIG.algorithm,
    59â†’      salt: salt as unknown as BufferSource,
    60â†’      iterations: PBKDF2_CONFIG.iterations,
    61â†’      hash: PBKDF2_CONFIG.hash,
    62â†’    },
    63â†’    passphraseKey,
    64â†’    {
    65â†’      name: AES_CONFIG.algorithm,
    66â†’      length: AES_CONFIG.keyLength,
    67â†’    },
    68â†’    false, // key is not extractable
    69â†’    ['encrypt', 'decrypt']
    70â†’  );
    71â†’}
    72â†’
    73â†’/**
    74â†’ * Converts Uint8Array to base64url-encoded string
    75â†’ * @param data - Binary data to encode
    76â†’ * @returns Base64url-encoded string (URL-safe: -_ instead of +/)
    77â†’ */
    78â†’function uint8ArrayToBase64Url(data: Uint8Array): string {
    79â†’  // Convert to binary string
    80â†’  const binary = String.fromCharCode(...data);
    81â†’  // Encode as standard base64
    82â†’  const base64 = btoa(binary);
    83â†’  // Convert to base64url (replace + with -, / with _, remove = padding)
    84â†’  return base64.replace(/\+/g, '-').replace(/\//g, '_').replace(/=+$/, '');
    85â†’}
    86â†’
    87â†’/**
    88â†’ * Decodes base64url-encoded string to Uint8Array
    89â†’ * @param base64url - Base64url-encoded string
    90â†’ * @returns Decoded binary data
    91â†’ */
    92â†’export function base64UrlToUint8Array(base64url: string): Uint8Array {
    93â†’  // Convert base64url to standard base64
    94â†’  let base64 = base64url.replace(/-/g, '+').replace(/_/g, '/');
    95â†’  // Add padding if needed
    96â†’  while (base64.length % 4 !== 0) {
    97â†’    base64 += '=';
    98â†’  }
    99â†’  // Decode to binary string
   100â†’  const binary = atob(base64);
   101â†’  // Convert to Uint8Array
   102â†’  return Uint8Array.from(binary, c => c.charCodeAt(0));
   103â†’}
   104â†’
   105â†’/**
   106â†’ * Compresses data using gzip compression
   107â†’ * @param data - String data to compress
   108â†’ * @returns Compressed data as Uint8Array
   109â†’ */
   110â†’async function compressData(data: string): Promise<Uint8Array> {
   111â†’  const encoder = new TextEncoder();
   112â†’  const dataBytes = encoder.encode(data);
   113â†’
   114â†’  // Use CompressionStream API (available in modern browsers)
   115â†’  const compressionStream = new CompressionStream('gzip');
   116â†’  const writer = compressionStream.writable.getWriter();
   117â†’  const reader = compressionStream.readable.getReader();
   118â†’
   119â†’  // Write data to the compressor
   120â†’  writer.write(dataBytes);
   121â†’  writer.close();
   122â†’
   123â†’  // Read compressed data
   124â†’  const chunks: Uint8Array[] = [];
   125â†’  let done = false;
   126â†’  while (!done) {
   127â†’    const { value, done: readerDone } = await reader.read();
   128â†’    done = readerDone;
   129â†’    if (value) chunks.push(value);
   130â†’  }
   131â†’
   132â†’  // Concatenate all chunks
   133â†’  const totalLength = chunks.reduce((sum, chunk) => sum + chunk.length, 0);
   134â†’  const compressed = new Uint8Array(totalLength);
   135â†’  let offset = 0;
   136â†’  for (const chunk of chunks) {
   137â†’    compressed.set(chunk, offset);
   138â†’    offset += chunk.length;
   139â†’  }
   140â†’
   141â†’  return compressed;
   142â†’}
   143â†’
   144â†’/**
   145â†’ * Encrypts data with a passphrase
   146â†’ * New binary format: version(1) || salt(16) || iv(12) || compressed_ciphertext
   147â†’ * @param data - String data to encrypt (typically JSON)
   148â†’ * @param passphrase - User-provided passphrase
   149â†’ * @returns Base64url-encoded string containing version + salt + iv + ciphertext
   150â†’ */
   151â†’export async function encrypt(data: string, passphrase: string): Promise<string> {
   152â†’  // Step 1: Compress the data first
   153â†’  const compressedData = await compressData(data);
   154â†’
   155â†’  // Step 2: Generate random salt for key derivation
   156â†’  const salt = crypto.getRandomValues(new Uint8Array(PBKDF2_CONFIG.saltLength));
   157â†’
   158â†’  // Step 3: Derive encryption key from passphrase
   159â†’  const key = await deriveKey(passphrase, salt);
   160â†’
   161â†’  // Step 4: Generate random IV
   162â†’  const iv = crypto.getRandomValues(new Uint8Array(AES_CONFIG.ivLength));
   163â†’
   164â†’  // Step 5: Encrypt compressed data
   165â†’  const ciphertext = await crypto.subtle.encrypt(
   166â†’    {
   167â†’      name: AES_CONFIG.algorithm,
   168â†’      iv,
   169â†’    },
   170â†’    key,
   171â†’    compressedData as unknown as BufferSource
   172â†’  );
   173â†’
   174â†’  // Step 6: Concatenate raw bytes: version(1) || salt(16) || iv(12) || ciphertext
   175â†’  const ciphertextBytes = new Uint8Array(ciphertext);
   176â†’  const combined = new Uint8Array(1 + PBKDF2_CONFIG.saltLength + AES_CONFIG.ivLength + ciphertextBytes.length);
   177â†’  combined[0] = CURRENT_FORMAT_VERSION; // Version byte
   178â†’  combined.set(salt, 1); // Salt at position 1
   179â†’  combined.set(iv, 1 + PBKDF2_CONFIG.saltLength); // IV after salt
   180â†’  combined.set(ciphertextBytes, 1 + PBKDF2_CONFIG.saltLength + AES_CONFIG.ivLength); // Ciphertext after IV
   181â†’
   182â†’  // Step 7: Encode as base64url for URL-safe use
   183â†’  return uint8ArrayToBase64Url(combined);
   184â†’}
   185â†’
   186â†’/**
   187â†’ * Decompresses data using gzip decompression
   188â†’ * @param compressedData - Compressed data as Uint8Array
   189â†’ * @returns Decompressed string
   190â†’ */
   191â†’async function decompressData(compressedData: Uint8Array): Promise<string> {
   192â†’  // Use DecompressionStream API (available in modern browsers)
   193â†’  const decompressionStream = new DecompressionStream('gzip');
   194â†’  const writer = decompressionStream.writable.getWriter();
   195â†’  const reader = decompressionStream.readable.getReader();
   196â†’
   197â†’  // Write compressed data to the decompressor
   198â†’  await writer.write(compressedData as unknown as BufferSource);
   199â†’  writer.close();
   200â†’
   201â†’  // Read decompressed data
   202â†’  const chunks: Uint8Array[] = [];
   203â†’  let done = false;
   204â†’  while (!done) {
   205â†’    const { value, done: readerDone } = await reader.read();
   206â†’    done = readerDone;
   207â†’    if (value) chunks.push(value);
   208â†’  }
   209â†’
   210â†’  // Concatenate all chunks
   211â†’  const totalLength = chunks.reduce((sum, chunk) => sum + chunk.length, 0);
   212â†’  const decompressed = new Uint8Array(totalLength);
   213â†’  let offset = 0;
   214â†’  for (const chunk of chunks) {
   215â†’    decompressed.set(chunk, offset);
   216â†’    offset += chunk.length;
   217â†’  }
   218â†’
   219â†’  // Decode bytes to string
   220â†’  const decoder = new TextDecoder();
   221â†’  return decoder.decode(decompressed);
   222â†’}
   223â†’
   224â†’/**
   225â†’ * Decrypts data with a passphrase
   226â†’ * Supports both old (double-base64 JSON) and new (binary) formats
   227â†’ * @param encryptedData - Base64-encoded string from encrypt()
   228â†’ * @param passphrase - User-provided passphrase
   229â†’ * @returns Decrypted string data
   230â†’ * @throws Error if decryption fails (wrong passphrase or corrupted data)
   231â†’ */
   232â†’export async function decrypt(encryptedData: string, passphrase: string): Promise<string> {
   233â†’  try {
   234â†’    // Detect format by checking first character after base64 decode
   235â†’    // Old format: starts with '{' (JSON structure)
   236â†’    // New format: starts with version byte (0x01 for version 1)
   237â†’    let decodedBytes: Uint8Array;
   238â†’
   239â†’    try {
   240â†’      decodedBytes = base64UrlToUint8Array(encryptedData);
   241â†’    } catch (e) {
   242â†’      // If base64url decode fails, try standard base64 (old format)
   243â†’      try {
   244â†’        const binary = atob(encryptedData);
   245â†’        decodedBytes = Uint8Array.from(binary, c => c.charCodeAt(0));
   246â†’      } catch (e2) {
   247â†’        // Both decoding attempts failed - likely corrupted data
   248â†’        throw new Error('Decryption failed. Check your passphrase and try again.');
   249â†’      }
   250â†’    }
   251â†’
   252â†’    // Check format by looking at first byte
   253â†’    const firstByte = decodedBytes[0];
   254â†’
   255â†’    if (firstByte === CURRENT_FORMAT_VERSION) {
   256â†’      // New binary format: version(1) || salt(16) || iv(12) || compressed_ciphertext
   257â†’      if (decodedBytes.length < 1 + PBKDF2_CONFIG.saltLength + AES_CONFIG.ivLength) {
   258â†’        throw new Error('Invalid encrypted data: truncated');
   259â†’      }
   260â†’
   261â†’      // Extract components
   262â†’      const salt = decodedBytes.slice(1, 1 + PBKDF2_CONFIG.saltLength);
   263â†’      const iv = decodedBytes.slice(1 + PBKDF2_CONFIG.saltLength, 1 + PBKDF2_CONFIG.saltLength + AES_CONFIG.ivLength);
   264â†’      const ciphertext = decodedBytes.slice(1 + PBKDF2_CONFIG.saltLength + AES_CONFIG.ivLength);
   265â†’
   266â†’      // Derive decryption key from passphrase
   267â†’      const key = await deriveKey(passphrase, salt);
   268â†’
   269â†’      // Decrypt
   270â†’      const decrypted = await crypto.subtle.decrypt(
   271â†’        {
   272â†’          name: AES_CONFIG.algorithm,
   273â†’          iv,
   274â†’        },
   275â†’        key,
   276â†’        ciphertext
   277â†’      );
   278â†’
   279â†’      // Decompress the decrypted data
   280â†’      const decryptedBytes = new Uint8Array(decrypted);
   281â†’      return await decompressData(decryptedBytes);
   282â†’    } else if (firstByte === 123 || String.fromCharCode(firstByte) === '{') {
   283â†’      // Old JSON format (first byte is ASCII for '{' = 123)
   284â†’      // Decode base64 to get structured data
   285â†’      const encryptedJson = atob(encryptedData);
   286â†’      const encrypted: EncryptedData = JSON.parse(encryptedJson);
   287â†’
   288â†’      // Decode components from base64
   289â†’      const salt = Uint8Array.from(atob(encrypted.salt), c => c.charCodeAt(0));
   290â†’      const iv = Uint8Array.from(atob(encrypted.iv), c => c.charCodeAt(0));
   291â†’      const ciphertext = Uint8Array.from(atob(encrypted.data), c => c.charCodeAt(0));
   292â†’
   293â†’      // Derive decryption key from passphrase
   294â†’      const key = await deriveKey(passphrase, salt);
