/**
 * Generate old-format test vector for backward compatibility testing
 * Run with: npx tsx src/test/config/generateOldFormat.ts
 */

async function generateOldFormatVector() {
  // Old format encrypt implementation (before binary refactoring)
  const plaintext = 'Hello, old format!';
  const passphrase = 'test-passphrase-123';

  const PBKDF2_CONFIG = {
    algorithm: 'PBKDF2' as const,
    hash: 'SHA-256' as const,
    iterations: 100000,
    saltLength: 16,
  };

  const AES_CONFIG = {
    algorithm: 'AES-GCM' as const,
    keyLength: 256,
    ivLength: 12,
  };

  // Derive key
  const encoder = new TextEncoder();
  const passphraseBytes = encoder.encode(passphrase);
  const passphraseKey = await crypto.subtle.importKey(
    'raw',
    passphraseBytes,
    'PBKDF2',
    false,
    ['deriveKey']
  );

  const salt = crypto.getRandomValues(new Uint8Array(PBKDF2_CONFIG.saltLength));
  const key = await crypto.subtle.deriveKey(
    {
      name: PBKDF2_CONFIG.algorithm,
      salt: salt as unknown as BufferSource,
      iterations: PBKDF2_CONFIG.iterations,
      hash: PBKDF2_CONFIG.hash,
    },
    passphraseKey,
    {
      name: AES_CONFIG.algorithm,
      length: AES_CONFIG.keyLength,
    },
    false,
    ['encrypt', 'decrypt']
  );

  // Encrypt
  const iv = crypto.getRandomValues(new Uint8Array(AES_CONFIG.ivLength));
  const plaintextBytes = encoder.encode(plaintext);
  const ciphertext = await crypto.subtle.encrypt(
    { name: AES_CONFIG.algorithm, iv },
    key,
    plaintextBytes
  );

  // Old format: JSON with base64-encoded components, then base64 again
  function arrayToBase64(arr: Uint8Array): string {
    const binary = String.fromCharCode(...arr);
    return btoa(binary);
  }

  const encryptedData = {
    salt: arrayToBase64(salt),
    iv: arrayToBase64(iv),
    data: arrayToBase64(new Uint8Array(ciphertext)),
  };

  const encryptedJson = JSON.stringify(encryptedData);
  const finalEncoded = btoa(encryptedJson);

  console.log('Old format test vector:');
  console.log('Plaintext:', plaintext);
  console.log('Passphrase:', passphrase);
  console.log('Encoded:', finalEncoded);
  console.log('');
  console.log('Use this in tests:');
  console.log(`const OLD_FORMAT_VECTOR = {`);
  console.log(`  plaintext: '${plaintext}',`);
  console.log(`  passphrase: '${passphrase}',`);
  console.log(`  encrypted: '${finalEncoded}'`);
  console.log(`};`);
}

generateOldFormatVector().catch(console.error);

